function addTag(a){a=a.replace(/^\s+/,"").replace(/\s+$/,"");if(document.getElementById("adv-tags-input")){var d=document.getElementById("adv-tags-input");if(d.value.length>0&&!d.value.match(/,\s*$/)){d.value+=", ";}var c=new RegExp(a+",");if(!d.value.match(c)){d.value+=a+", ";}}else{var b=jQuery("#tags-input").val();b+=","+a;b=b.replace(/\s+,+\s*/g,",").replace(/,+/g,",").replace(/,+\s+,+/g,",").replace(/,+\s*$/g,"").replace(/^\s*,+/g,"");jQuery("#tags-input").val(b);tagBox.quickClicks();}}